String.prototype.includes = function(s) {
    return this.toString().replace(s, '') !== this.toString();
};

String.prototype.startsWith = function(s) {
    var ret = true;
    for(i = 0; i < s.length; i++) {
        if(this.toString()[i] != s[i]) {
            ret = false;
            break;
        }
    }
    return ret;
};

