$(function() {
    var bindTabClick;

    (bindTabClick = function() {
        $('tabbar tab:not([newtab]):not([disabled])').click(function() {
            $(this).parent().find('> tab').removeAttr('active');
            $(this).attr('active', '');

            var pageid = $(this).parent().parent().attr('id');
            var to = $(this).attr('page');

            $('tabcontent[tab="' + pageid + '"] page').removeAttr('active');
            $('tabcontent[tab="' + pageid + '"] page#' + to).attr('active', '');
        });

        $('tabbar tab:not([disabled]) close').click(function() {
            var pageid = $(this).parent().parent().attr('page');
            var tabid = $(this).parent().parent().parent().parent().attr('id');
            var page = $('tabcontent[tab="' + tabid + '"] page#' + pageid);

            $(this).parent().parent().remove();
            if(page.attr('active') !== undefined) {
                $('tabcontent[tab="' + tabid + '"] page').removeAttr('active');
                $('tabcontent[tab="' + tabid + '"] page:first-child').attr('active', '');

                $('tabbar#' + tabid + ' tab[active]').removeAttr('active');
                $('tabbar#' + tabid + ' tab:first-of-type').attr('active', '');
            }
            page.remove();
        });
    })();

    $('tabbar tab[newtab]').click(function() {
        var pageid = 'newtab-page-' + Math.floor(Math.random() * 1e8);
        var newtab = $('<tab page="' + pageid + '"><t>' + ($(this).attr('text') || '새 탭') + ' <close>×</close></t></tab>');

        $(this).before(newtab);
        $('tabcontent[tab="' + $(this).parent().parent().attr('id') + '"]').append('<page id="' + pageid + '">' + ($(this).attr('content') || '') + '</page>');
        bindTabClick();

        newtab.click();
    });
});

$(function() {
    var tlf = null,
        nosl = 1,
        sa;

    $('.mc-light').on('mousedown', function() {
        var light = $(this).find('> span.light');
        var light2 = $(this).find('> span.light2');
        nosl = 0;

        light.css({
            transition: 'all .65s',
            margin: '0 0 0 120%'
        });

        setTimeout(function() {
            light.css({
                transition: 'all 0s',
                margin: '0 0 0 -120%'
            });

            function slowlight() {
                light2.css({
                    transition: 'all 3.25s',
                    margin: '0 0 0 120%'
                });

                setTimeout(function() {
                    light2.css({
                        transition: 'all 0s',
                        margin: '0 0 0 -120%'
                    });
                }, 3250);
            }

            if(light2 && !nosl) {
                tlf = setInterval(slowlight, 4250);
                setTimeout(slowlight, 350);
            }
        }, 500);
    });

    $('body').on('mouseup mouseout', sa = function() {
        clearInterval(tlf), nosl = 1;

        $('.mc-light > span.light2').css({
            transition: 'all 0s',
            margin: '0 0 0 -120%'
        });
    });

    $('.mc-light').on('mouseout', sa);
});

(function() {
	function saveSetting(name, val) {
		var d = new Date; 
		d.setDate(d.getDate() + 180);
		document.cookie = escape(name) + '=' + escape(val) + ';';
	}

	function getSetting(name, def) {
		var ret;
		if(!document.cookie) {
			saveSetting(name, def);
			return def;
		}

		const c = document.cookie.split(escape(name) + '=');
		if(c.length < 2) {
			saveSetting(name, def);
			return def;
		}

		return unescape(c[1].split(';')[0]);
	}
	
	$(function() {
		var ss = document.querySelectorAll('.scheme-stylesheet');
		
		for(var eli=0; eli<ss.length; eli++) {
			var el = ss[eli];
			if(!(el.getAttribute('id').startsWith(getSetting('scheme', 'bluemetal')))) el.remove();
		}
	});

	/* window.saveSetting = saveSetting, window.getSetting = getSetting; */
	
	$(function () {
		var windowCount = 0;
	
		if($(window).width() >= 1080) $("grp.for-mobile").remove();

		const html = {
			escape: function(content) {
				content = content.replace(/["]/gi, '&quot;');
				content = content.replace(/[&]/gi, '&amp;');
				content = content.replace(/[<]/gi, '&lt;');
				content = content.replace(/[>]/gi, '&gt;');
	
				return content;
			}
		};

		window.memoCount = 0;

		const getid = function(id) { return document.getElementById(id) }
		const qall = function(q) { return document.querySelectorAll(q) }

		var px, py, ox, oy;

		function setDraggable() {
			$('div.window').draggable({ handle: '.titlebar' });

			$('div.window').children('.titlebar').on('mousedown', function(e) {
				$(this).parent().appendTo($(this).parent().parent());
			});

			$('div.window:not(#toolsWindow)').resizable({
				minWidth:  '160px',
				minHeight: '60px'
			});
			
			$('div.window .close-btn').click(function() {
				win = $(this);
			
				function closeAction() {
					$('.window#windowManager .window-content').children('li[data-winid="' + win.parent().attr('id') + '"]').remove();
					win.parent().remove();
					$('a[data-setaswindow="1"]').removeAttr('data-setaswindow');
				}
				
				if(getSetting('noAnimations') == '1') 
					closeAction();
				else 
					win.parent().slideToggle('fast', closeAction);
			});
		
			$('.window#windowManager .window-content li').click(function() {
				const win = $('.window#' + $(this).attr('data-winid'));
				win.appendTo(win.parent());
			});
		
			$('.window#windowManager .window-content li a.close-link').click(function() {
				$('.window#' + $(this).parent().attr('data-winid')).children('a.close-btn').click();
			});
		}

		$('div.window .fold-btn').click(function() {
			$(this).next().slideToggle('fast');
		});

		setDraggable();

		function createWindow(title, content, width, height, top, left, closeable) {
			if(typeof(closeable) == 'undefined') closeable = 1;
			const newWindow = $('<div class=window id="' + String(++windowCount) + '">' +
					'<div class=titlebar><div class=title>' + html.escape(title) + '</div></div>' +
					'<a ' + (!closeable ? 'style="display: none;"' : '') + ' title="닫기" class=close-btn>' + "<img src='data:image/png;base64,' />" + '</a>' +
					'<div class=window-content style="' + (getSetting('noAnimations') == '1' ? '' : 'display: none;') + '">' + content + '</div>' +
			  '</div>');
			newWindow.css({
				'width': width ? width : '480px',
				'height': height ? height : '360px',
				'top': top ? top : Number($('.window:last-child').css('top').replace(/px$/, '')) + 30 + 'px',
				'left': left ? left : Number($('.window:last-child').css('left').replace(/px$/, '')) + 30 + 'px'
			}).appendTo('grp.for-pc#windows');
			
			$('<li data-winid="' + String(windowCount) + '">' + html.escape(title) + (closeable ? ' <a class=close-link>×</a>' : '') + '</li>').appendTo($(".window#windowManager .window-content"));

			setDraggable();
			
			if(getSetting('noAnimations') != '1') newWindow.children('.window-content').slideToggle('fast');

			return newWindow;
		}

		window.createWindow = createWindow;

		$('a#skinSettingsLink').click(function() {
			const settingsWindow = createWindow('설정', 
				'<div class=form-group>' +
					'<div>' +
						'<h2>창</h2>' + 
						'<label><input type=checkbox id=winManagerOnTop onclick="if($(this).is(\':checked\')) saveSetting(\'winManagerOnTop\', \'1\'); else saveSetting(\'winManagerOnTop\', \'0\'); setSettings();"> 창 전환기를 항상 위에 표시</label><br>' + 
						'<label><input type=checkbox id=noAnimations onclick="if($(this).is(\':checked\')) saveSetting(\'noAnimations\', \'1\'); else saveSetting(\'noAnimations\', \'0\'); setSettings();"> 애니메이션 끄기</label><br>' + 
					'</div>' +
					
					'<div>' +
						'<h2>모양</h2>' +
						'<label>스킨 [*]:</label><br>' +
						'<select class=form-control id=schemeSelect onchange="saveSetting(\'scheme\', this.value);">' +
							'<option value=glossy ' + (getSetting('scheme') == 'glossy' ? 'selected' : '') + '>푸른하늘 </option>' +
							'<option value=bluemetal ' + (getSetting('scheme') == 'bluemetal' ? 'selected' : '') + '>금속</option>' +
							'<option value=flat ' + (getSetting('scheme') == 'flat' ? 'selected' : '') + '>심플</option>' +
						'</select>' +
					'</div>' +
					 
					'<p>[*] 새로고침해야 적용됩니다.</p>' +
				'</div>', '420px', '350px');

			if(getSetting('noLinkAsWindow') == '1') $("#noLinkAsWindow").attr('checked', '');
			if(getSetting('winManagerOnTop') == '1') $("#winManagerOnTop").attr('checked', '');
			if(getSetting('noAnimations') == '1') $("#noAnimations").attr('checked', '');
		});

		function setSettings() {
			if(getSetting('winManagerOnTop') == '1') $("#windowManager").css('z-index', '11');
			else $("#windowManager").css('z-index', '9');
		}

		setSettings();

		window.setSettings = setSettings;
	});
})();

$(function() {
	var bksp = 0;
	var lastval = $('#tagInput').val();
	
	function bdb() {
		$('div#tags span.tag a.delete').click(function() {
			$(this).parent().remove();
		});
	}
	
	function setEdit() {
		$('div#tags span.tag').click(function (e) { 
			if(e.target == this) {
				$('input#tagInput').val($(this).find('> input').val()).focus();
				$(this).remove();
			}
		});
	}
	
	function insertTag(tag) {
		const tagspan = $('<span class="tag" title="편집하려면 누르십시오."></span>');
		tagspan.text(tag).html(tagspan.html() + '<a class="delete" title="분류 해제">×</a>' + '<input name="categories" />').find('> input').attr('value', tag);
		$('div#tags input#tagInput').before(tagspan);
		bdb();
		
		setEdit();
	}
	
	var ajaxt = 0;

	$('#tagInput').on('keyup', function(e) {
		switch(e.keyCode || e.charCode) {
			case 13:
				if($(this).val().replace(/\s/g, '') == '') break;
				const tag = $(this).val();
				if(!tag) break;
				
				insertTag(tag);
				
				$(this).val('');
				bdb();
			break; case 8: case 46:
				if($(this).val() || lastval) break;
				
				const lasttag = $('div#tags span.tag').last().find('> input').val();
				
				$('div#tags span.tag').last().remove();
				$('#tagInput').val(lasttag);
				bksp = 0;
		}
		lastval = $(this).val();
		
		if(ajaxt) return;
		ajaxt = 1; setTimeout(function() {
			ajaxt = 0;
		}, 500);
		
		$('div#tagSearch').html('');
		
		const data = [
			{ title: '동물', count: 3, description: '' },
			{ title: '전자', count: 435, description: '' },
			{ title: '탈것', count: 34, description: '' },
			{ title: '과학', count: 7, description: '' },
		];
		const query = $(this).val();
		
		$.ajax({
			type: 'GET',
			url: '/category_search/' + encodeURIComponent(query),
			dataType: 'json',
			success: function(data) {
				for(i in data) {
					const item = data[i];
					if(!item.title.toLowerCase().includes(query.toLowerCase()) || !query) continue;
					
					const card = $('<div class="card"><span class="category-title"></span><span class="description"></span></div>');
					card.find('> .category-title').text(item.title);
					card.find('> .description').text('문서 ' + item.count + '개');
					
					$('div#tagSearch').append(card);
				}
				
				$('div#tagSearch div.card').click(function() {
					insertTag($(this).find('> .category-title').text());
					$('#tagInput').val('').focus();
					$(this).parent().html('');
				});
			}
		});
	});
	
	$('div#tags').click(function(e) { 
		if(e.target == this) $('input#tagInput').focus();
	});
	
	bdb();
	setEdit();
	
	$('input#tagInput').on('keydown', function(e) {
		if(e.keyCode == 13) {
			e.preventDefault();
			return false;
		}
	});
});
